    healthcheck:
      test: [ "CMD", "curl", "-f", "http://localhost:3000/" ]
      interval: 30s
      timeout: 10s
      retries: 3

  db:
    image: postgres:15-alpine
    container_name: affine-db
    environment:
      POSTGRES_USER: affine
      POSTGRES_PASSWORD: affine
      POSTGRES_DB: affine
    volumes:
      - affine_db_data:/var/lib/postgresql/data
    networks:
      - affine_net
    healthcheck:
      test: [ "CMD-SHELL", "pg_isready -U affine" ]
      interval: 5s
      timeout: 5s
      retries: 5

  redis:
    image: redis:7-alpine
    container_name: affine-redis
    networks:
      - affine_net
    healthcheck:
      test: [ "CMD", "redis-cli", "ping" ]
      interval: 5s
      timeout: 5s
      retries: 5

networks:
  affine_net:
    driver: bridge

volumes:
  affine_db_data:
    name: affine_db_data

INNER_EOF

