      - SMTP_USER_NAME=
      - SMTP_USER_PWD=
      - SMTP_SSL_Enabled=false
    volumes:
      - ./geoip:/geoip:ro

  plausible_db:
    image: postgres:14-alpine
    container_name: plausible_db
    restart: unless-stopped
    volumes:
      - plausible_db_data:/var/lib/postgresql/data
    environment:
